import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { User } from "./../../components/interface/user";
import { environment } from "../../../../environment";
import { Observable } from "rxjs";
import jwtDecode from "jwt-decode";

@Injectable({
  providedIn: "root",
})
export class AuthService {
  currentLogUser = null;
  private token!: String;
  private apiBaseUrl = environment.apiBaseUrl;
  constructor(private _HttpClient: HttpClient) {}

  detachToken() {
    this.token? this.token = localStorage.getItem("token") : ""
    this.token = 
    this.currentLogUser = jwtDecode(this.token);
  }

  register(user: User) {
    return this._HttpClient.post<any>(`${this.apiBaseUrl}/register`, user);
  }

  login(formData: any): Observable<any> {
    return this._HttpClient.post(`${this.apiBaseUrl}/login`, formData);
  }
}
